import React, { useState } from "react";
import './Services.css'
import HeartEmoji from "../../img/heartemoji.png";
import Glasses from "../../img/glasses.png";
import Humble from "../../img/humble.png"
import Card from "../Card/Card";
import Resume from './Resume.pdf';
import { useSpring, animated } from 'react-spring';
const Services = () =>{
    const [isHovered, setIsHovered] = useState(false);

    const emojiProps = useSpring({
        transform: isHovered ? 'scale(1.5)' : 'scale(1)',
        config: { tension: 100, friction: 10 }
    });
    return(
        <div className="services" id="Services">
            { /* left side */}
            <div className="awesome">
            <span></span>
            <span>Coordonnées et Spécialités</span>
            <spane>
                Je m'appelle ANDRIAMANAMPY Hajatiana, j'ai 25 ans et je suis un jeune diplômé et passioné dans le <br/>
                domaine informatique. Je faisais mes études universitaires à l'Ecole Nationale d'Informatique abrégée ENI<br/>
                 et j'ai obtenu mon diplôme de Master professionel.<br/>
                 <ul>
                    <li>Date et Lieu de naissance : 28 Février 1999 à Ambositra</li>
                    <li>Diplôme :  Master II en Informatique</li>
                    <li>Ambohipo, bloc 10 Antananarivo</li>
                    <li>Contact : +2619690760</li>
                    <li>Adresse E-mail : andriamanampyhajatiana.io@gmail.com</li>
                    <li>Freelance : possible</li>
                    <li>5 Ans d'Experiences</li>
                    <li>Plus de 20 projets réalisés</li>
                 </ul>
                
            </spane>
            <a href={Resume} download>
            <button className="button s-button">Download CV</button>
            </a>
            
            <div className="blur s-blur1" style={{background: "#ABF1FF94" }}></div>

            </div>
            {/* right side */}
            <div className="cards">
                <div style={{left: '14rem'}}>
                    <Card
                    emoji = {animated.HeartEmoji}
                        onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            style={emojiProps}
            role="img"
            aria-label="emoji"
                    
                    heading = {'Design'}
                    detail = {"Figma, Photoshop, Adobe, Adobe xd"}
                    />
                </div>

            {/* second card */}
            <div style={{top: '12rem', left: '-4rem'}}>
                <Card
                emoji={Glasses}
                heading={"Developpeur"}
                detail={"HTML et CSS, Javascript, React.js,Next.js,taiwindcss,typescript, Node.js, BD: MySql, postgreSql..."}
                />
            </div>
            {/* 3rd Card*/}
            <div style={{top: '19rem', left: '12rem'}}>
               <Card
                emoji={Humble}
                heading={"Systéme d'exploitation et plateforme d'hebergement: "}
                detail={
                    'Windows, Linux: Ubuntu, Debian, parotte, kali, github, gitlab'
                }
               /> 
            </div>
            <div className="blur s-blur2" style={{background: "var(--purple)"}}></div>

            </div>
        </div>
    )

   
}
export default Services;
