import React, { useRef, useState } from "react";
import emailjs from '@emailjs/browser';
import './Contact.css'
const Contact = () =>{

    const form = useRef();

    const [done, setDone] = useState(false)

    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_cuw9syf', 'template_2gmp7aq', form.current, {
          publicKey: 'dthJdrlqNzmJLmvGd',
        })
        .then((result) => {
            console.log(result.text);
            setDone(true);
          },(error) => {
            console.log(error.text);
          },
        );
    };

    return(
        <div className="contact-form">
            <div className="w-left">
                <div className="awesome">
                    <span>Get in touch</span>
                    <span>Contacter moi</span>
                    <div
                    className="blur s-blur1"
                    style={{background: "#ABF1FF94"}}
                    ></div>
                </div>
            </div>
            <div className="c-right">
                <form ref={form} onSubmit={sendEmail}>
                    <input type="text" name="user-name" className="user" placeholder="Name" />
                    <input type="email" name="user_email" className="user" placeholder="Email" />
                    <textarea name="message" className="user" placeholder="Message"/>
                    <input type="submit" value="Send" className="button" />
                    <span>{done && "Merci de m'avoir contacter"}</span>
                    <div
                    className="blur c-blur1"
                    style={{background: "var(--purple)"}}
                    ></div>
                </form>
            </div>
        </div>
    )
}
 
export default Contact
